mapper.select:
SELECT

FROM user_ u

WHERE u.user_id = #{userId}

SELECT

FROM user_ u

WHERE u.username = #{username}

SELECT

FROM user_ u

WHERE u.email = #{email}

SELECT

FROM user_ u


	1 = 1

		AND u.user_id = #{userId}


		AND u.username like CONCAT('%', #{username}, '%')


		JOIN authority au ON au.user_id = u.user_id AND au.authorityName = #{authorityName}


		AND u.enable = #{enable}


ORDER BY u.user_id
